# REFERENCES:# https://docs.micropython.org/en/latest/esp8266/tutorial/ssd1306.html# https://github.com/peterhinch/micropython-font-to-py/tree/master/writerimport machineimport ssd1306import timefrom writer import Writerimport courier20  # Font to use#-- GPIOs --sclPIN,sdaPIN = 33, 34consPIN, colPIN, vowelPIN, downPIN =7,8,9,10#-- OLED --oledWIDTH = 128oledHEIGHT =  32#-- INIT OLED --i2c = machine.SoftI2C( machine.Pin(sclPIN), machine.Pin(sdaPIN) )oled = ssd1306.SSD1306_I2C( oledWIDTH, oledHEIGHT, i2c )wri = Writer(oled, courier20)#-- INIT BUTTONS --colBtn = machine.Pin(colPIN, machine.Pin.IN, machine.Pin.PULL_UP)vowelBtn = machine.Pin(vowelPIN, machine.Pin.IN, machine.Pin.PULL_UP)consBtn = machine.Pin(consPIN, machine.Pin.IN, machine.Pin.PULL_UP)downBtn = machine.Pin(downPIN, machine.Pin.IN, machine.Pin.PULL_UP)x,y=0,0def readButtons():    global x,y    if colBtn.value()==0:        x = x+1 #if x<(oledWIDTH-1) else 0    if vowelBtn.value() == 0:        x = x-1 #if x>1 else oledWIDTH-1    if consBtn.value() == 0:        y = y+1 #if y<(oledHEIGHT-1) else 0    if downBtn.value() == 0:        y = y-1 #if y>1 else oledHEIGHT-1    oled.fill(0)    Writer.set_textpos(oled, y, x)    wri.printstring("M")    oled.show()    time.sleep(.1)        #== MAIN ==while True:    readButtons()